import { createSlice } from '@reduxjs/toolkit';
import { createAsyncThunk ,serializableError} from '@reduxjs/toolkit';
import axios from 'axios';

const createConfig = () => {
    const token = localStorage.getItem('token');
    if (token) {
      return {
        headers: {
          token
        }
      };
    } else {
      return {};
    }
  }
  
  
  
  
  export const chatbot = createAsyncThunk('chat/chatbot',  async ({ text, currentModel }, { rejectWithValue }) => {
      const config = createConfig();
    
      try {
        // Make the API call to the chatbot endpoint
        const response = await axios.post('http://localhost:5000/api/chat/chatbot', { text, currentModel }, config);
        const generatedContent = response.data.generatedContent;
    
        // Create the new user, chatbot, and model messages
        const newMessage = { role: 'user', content: text };
        const botMessage = { role: 'chatbot', content: generatedContent };
        const modelMessage = { role: 'model', content: currentModel };
    
        // Return an object with the created messages
        return { newMessage, botMessage, modelMessage };
      }     catch (error) {
        console.error('Error fetching chatbot data:', error);
        return rejectWithValue(error.message || 'Failed to fetch chatbot data. Please try again.');
      }
    },
    {
      rejectionReducer: (state, action) => {
        if (action.payload) {
          // Extracting a serializable error message
          return { ...state, error: action.payload.message };
        }
        return state;
      },
    }
  );

    
 
  export const modelschat= createAsyncThunk('chat/modelschat', async (_, { rejectWithValue }) => {
    const config = createConfig();
  
  
    try {
  
  
      const { data } = await axios.get('http://localhost:5000/api/chat/models', config);
  

    return data ;
    } catch (error) {
      // Log the error message
      console.error('Error', error);
  
      return rejectWithValue(error.response.data.message);
    }
  });

const initialState = {

  isLoading: false,
  registerErrors: null,
  msg: null,
  chat: JSON.parse(localStorage.getItem('chat')) || [],
  messageuser:{ },
  models:[]

};

const chatSlice = createSlice({
  name: 'chat',
  initialState,
  reducers: {
  
    resetChatState: (state) => {
        state.isLoading = false;
        state.registerErrors = null;
        state.chat = [];
      },


  },
  extraReducers: (builder) => {
    builder
      .addCase(chatbot.pending, (state) => {
        state.isLoading = true;



      })
      .addCase(chatbot.fulfilled, (state, { type, payload }) => {
        state.isLoading = false;
        state.messageuser=payload.newMessage

        state.chat.push(payload.newMessage, payload.botMessage );
        localStorage.setItem('chat', JSON.stringify(state.chat));


       
      })
  
     
      .addCase(modelschat.pending, (state) => {
        state.isLoading = true;



      })
      .addCase(modelschat.fulfilled, (state, { type, payload }) => {
        state.isLoading = false;

        state.models= payload
        localStorage.setItem('models', JSON.stringify(state.models));


       
      })
  




      
  }

});

export const { resetChatState } = chatSlice.actions;

export default chatSlice.reducer;
