import React, { useRef, useEffect } from 'react';
import '../css/chat.css';
import '../normal.css';
import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { chatbot } from '../Redux/ChatSlice';
import { resetChatState } from '../Redux/ChatSlice';

const Home = () => {
  const [text, setText] = useState('');
  const chat = useSelector((state) => state.ChatReducer.chat);
  const messageuser = useSelector((state) => state.ChatReducer.messageuser);
  const dispatch = useDispatch();
  const inputRef = useRef(null);

  useEffect(() => {
    // Focus on the input area when messages are updated
    inputRef.current.focus();
  }, [chat, messageuser]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    await dispatch(chatbot({ text }));
    await setText('');
  };

  const handleNewChat = () => {
    dispatch(resetChatState());
  };

  return (
    <div className='chathome'>
      <aside className='sidemenu'>
        <div className='side-menu-button' onClick={handleNewChat}>
          <span style={{ paddingLeft: '6px', paddingRight: '12px', width: '1rem' }}>+</span> New Chat
        </div>
      </aside>
      <section className='chatbox'>
        <div className='chat-input-holder'>
          <form onSubmit={handleSubmit}>
            <input
              ref={inputRef}
              className='chat-input-textarea'
              rows='1'
              placeholder='type your message'
              value={text}
              onChange={(e) => {
                setText(e.target.value);
              }}
            ></input>
          </form>
        </div>
     
         
      </section>
    </div>
  );
};

export default Home;
