import React, { useState } from 'react';

function App() {
  const [products, setProducts] = useState([
    { name: 'Banana', vote: 0 },
    { name: 'Orange', vote: 0 }
  ]);

  const handleIncrement = (productName) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.name === productName
          ? { ...product, vote: product.vote + 1 }
          : product
      )
    );
  };

  const handleDecrement = (productName) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.name === productName
          ? { ...product, vote: Math.max(product.vote - 1, 0) }
          : product
      )
    );
  };

  return (
    <div className="App">
      {products.map((item) => (
        <div key={item.name}>
          <p>{item.name}</p>
          <p>Votes: {item.vote}</p>
          <button onClick={() => handleIncrement(item.name)}>+</button>
          <button onClick={() => handleDecrement(item.name)}>-</button>
        </div>
      ))}
    </div>
  );
}

export default App;
