import { createSlice } from '@reduxjs/toolkit';
import { createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

const createConfig = () => {
    const token = localStorage.getItem('token');
    if (token) {
      return {
        headers: {
          token
        }
      };
    } else {
      return {};
    }
  }
  
  
  
  
  export const chatbot = createAsyncThunk('chat/chatbot', async ( { text }, { rejectWithValue }) => {
    const config = createConfig();
  
    try {
  
  
      const { data } = await axios.post('http://localhost:5000/api/chat/chatbot', { text } , config);
  
      return data;
    } catch (error) {
      // Log the error message
      console.error('Error adding facture:', error);
  
      return rejectWithValue(error.response.data.message);
    }
  });

 


  const chatSlice = createSlice({
    name: 'chat',
    initialState,
    reducers: {
      resetChatState: (state) => {
        state.isLoading = false;
        state.registerErrors = null;
        state.chat = [];
      },
      setInitialMessages: (state, action) => {
        state.chat = action.payload;
      },
      addMessageToChat: (state, action) => {
        state.chat.push(action.payload);
      },
    },
  extraReducers: (builder) => {
    builder
      .addCase(chatbot.pending, (state) => {
        state.isLoading = true;
      })
      .addCase(chatbot.fulfilled, (state, { type, payload }) => {
        state.isLoading = false;
        state.chat = payload.generatedContent
       
      })
      .addCase(chatbot.rejected, (state, { type, payload }) => {
      })
     





      
  }

});

export const { resetChatState } = chatSlice.actions;

export default chatSlice.reducer;
