import React from 'react'
import '../css/chat.css';
import '../normal.css'
import  { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from 'react-redux';
import { chatbot } from '../Redux/ChatSlice';
import { resetChatState } from '../Redux/ChatSlice';
const Home = () => {
    const [text, setText] = useState("");
    
    const  chat= useSelector(state => state.ChatReducer.chat)
    const  messageuser= useSelector(state => state.ChatReducer.messageuser)
    const [userMessages, setUserMessages] = useState([]);
    const [chatbotMessages, setChatbotMessages] = useState([]);
  
    useEffect(() => {
      const userMsgs = chat.filter(message => message.role === 'user');
      const chatbotMsgs = chat.filter(message => message.role === 'chatbot');
  
      // Delay showing user messages by 2 seconds
      const delay = 2000;
      const userTimeout = setTimeout(() => {
        setUserMessages(userMsgs);
      }, delay);
  
      setChatbotMessages(chatbotMsgs);
  
      // Clear timeout on component unmount
      return () => clearTimeout(userTimeout);
    }, [chat]);

    const dispatch=useDispatch()
    const handleSubmit = async (e) => {
        e.preventDefault();
        dispatch(chatbot({text}))
          setText('')

      };
      const handleNewChat = () => {
        dispatch(resetChatState());
      };
      
  return (
    <div className='chathome'>

 <aside className='sidemenu'>
    <div className='side-menu-button' onClick={handleNewChat}><span style={{paddingLeft:'6px', paddingRight:'12px', width:'1rem'}}>+</span> New Chat</div>
 </aside>
<section className='chatbox'>

<div className='chat-input-holder'>
 <form onSubmit={handleSubmit} ><input className='chat-input-textarea' rows='1' placeholder='type your message'   value={text}
          onChange={(e) => {
            setText(e.target.value);
          }}></input></form>
</div>


<div className='chat-log'>
<div className='chat-message'>
{userMessages.map((userMessage, index) => (


      <div key={index} className={`chat-message`}>
        <div className='chat-message-center'>
        <div className='avatar'>
      
          </div>
          <div className='message'>{userMessage.content}</div>
        </div>
      </div>

    ))}
                </div>


  {/* Render AI responses */}

      <div key={index} className={`chat-message chatgpt`}>
        <div className='chat-message-center'>
          <div className={`avatar avatargpt`}>
            {aiMessage.role === 'chatbot' && (
              <svg
              xmlns="http://www.w3.org/2000/svg"
              width={39}
              height={39}
              fill="none"
              className="icon-sm"
