    />
  </svg>}
      
         </div>
     
         <div className='message'>
    
         {message.role === 'user' && (
            <div className='user-message'>
              {message.content}
            </div>
          )}
          {message.role === 'chatbot' && (
            <div className='chatbot-message'>
              {message.content}
            </div>
          )}
            </div>
      </div>
    </div>
  ))} 


</div>

  </div>


</section>



    </div>
  )
}

export default Home
