import { createSlice } from '@reduxjs/toolkit';
import { createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

const createConfig = () => {
    const token = localStorage.getItem('token');
    if (token) {
      return {
        headers: {
          token
        }
      };
    } else {
      return {};
    }
  }
  
  
  
  
  export const chatbot = createAsyncThunk('chat/chatbot', async ( { text }, { rejectWithValue }) => {
    const config = createConfig();
  
    try {
  
  
      const { data } = await axios.post('http://localhost:5000/api/chat/chatbot', { text } , config);
  
      const newMessage = { role: 'user', content: text };
    const botMessage = { role: 'chatbot', content: data.generatedContent };

    return { newMessage, botMessage };
    } catch (error) {
      // Log the error message
      console.error('Error adding facture:', error);
  
      return rejectWithValue(error.response.data.message);
    }
  });

 


