  isLoading: false,
  registerErrors: null,
  msg: null,
  chat: JSON.parse(localStorage.getItem('chat')) || [],
  messageuser

};

const chatSlice = createSlice({
  name: 'chat',
  initialState,
  reducers: {
  
    resetChatState: (state) => {
        state.isLoading = false;
        state.registerErrors = null;
        state.chat = [];
      },


  },
  extraReducers: (builder) => {
    builder
      .addCase(chatbot.pending, (state) => {
        state.isLoading = true;



      })
      .addCase(chatbot.fulfilled, (state, { type, payload }) => {
        state.isLoading = false;
        state.messageuser=payload.newMessage

        state.chat.push(payload.newMessage, payload.botMessage);
        localStorage.setItem('chat', JSON.stringify(state.chat));


       
      })
      .addCase(chatbot.rejected, (state, { type, payload }) => {
      })
     





      
  }

});

export const { resetChatState } = chatSlice.actions;

export default chatSlice.reducer;
